import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_KEY = 'YOUR_NEWS_API_KEY'; // Replace with your NewsAPI key
const BASE_URL = 'https://newsapi.org/v2';

function App() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [category, setCategory] = useState('general');

  const categories = [
    'general',
    'business',
    'technology',
    'science',
    'health',
    'sports',
    'entertainment'
  ];

  useEffect(() => {
    fetchNews();
  }, [category]);

  const fetchNews = async (search = '') => {
    try {
      setLoading(true);
      setError(null);
      const endpoint = search
        ? `${BASE_URL}/everything?q=${encodeURIComponent(search)}&apiKey=${API_KEY}`
        : `${BASE_URL}/top-headlines?country=us&category=${category}&apiKey=${API_KEY}`;

      const response = await axios.get(endpoint);
      setArticles(response.data.articles);
    } catch (err) {
      setError('Failed to fetch news. Please try again later.');
      console.error('Error fetching news:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      fetchNews(searchQuery);
    }
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="app">
      <header>
        <h1>ðŸ“° News Explorer</h1>
        <form onSubmit={handleSearch} className="search-form">
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="Search for news..."
          />
          <button type="submit">Search</button>
        </form>
        <div className="categories">
          {categories.map(cat => (
            <button
              key={cat}
              onClick={() => setCategory(cat)}
              className={`category-btn ${category === cat ? 'active' : ''}`}
            >
              {cat.charAt(0).toUpperCase() + cat.slice(1)}
            </button>
          ))}
        </div>
      </header>

      <main className="news-grid">
        {articles.map((article, index) => (
          <article key={index} className="news-card">
            {article.urlToImage && (
              <img
                src={article.urlToImage}
                alt={article.title}
                className="article-image"
              />
            )}
            <div className="article-content">
              <h2>{article.title}</h2>
              <p className="article-meta">
                {article.source.name} â€¢ {formatDate(article.publishedAt)}
              </p>
              <p className="article-description">{article.description}</p>
              <a
                href={article.url}
                target="_blank"
                rel="noopener noreferrer"
                className="read-more"
              >
                Read More â†’
              </a>
            </div>
          </article>
        ))}
      </main>
    </div>
  );
}

export default App;
